        bool hasPrefix = (end > begin + 1) && bytes2(_unsafeReadBytesOffset(bytes(input), begin)) == bytes2("0x"); // don't do out-of-bound (possibly unsafe) read if sub-string is empty
        uint256 expectedLength = 40 + hasPrefix.toUint() * 2;

        // check that input is the correct length
        if (end - begin == expectedLength) {
            // length guarantees that this does not overflow, and value is at most type(uint160).max
            (bool s, uint256 v) = _tryParseHexUintUncheckedBounds(input, begin, end);
            return (s, address(uint160(v)));
        } else {
            return (false, address(0));
        }
    }

    function _tryParseChr(bytes1 chr) private pure returns (uint8) {
        uint8 value = uint8(chr);

        // Try to parse `chr`:
        // - Case 1: [0-9]
        // - Case 2: [a-f]
        // - Case 3: [A-F]
        // - otherwise not supported
        unchecked {
            if (value > 47 && value < 58) value -= 48;
            else if (value > 96 && value < 103) value -= 87;
            else if (value > 64 && value < 71) value -= 55;
            else return type(uint8).max;
        }

        return value;
    }

    /**
     * @dev Escape special characters in JSON strings. This can be useful to prevent JSON injection in NFT metadata.
     *
     * WARNING: This function should only be used in double quoted JSON strings. Single quotes are not escaped.
     *
     * NOTE: This function escapes all unicode characters, and not just the ones in ranges defined in section 2.5 of
     * RFC-4627 (U+0000 to U+001F, U+0022 and U+005C). ECMAScript's `JSON.parse` does recover escaped unicode
     * characters that are not in this range, but other tooling may provide different results.
     */
    function escapeJSON(string memory input) internal pure returns (string memory) {
        bytes memory buffer = bytes(input);
        bytes memory output = new bytes(2 * buffer.length); // worst case scenario
        uint256 outputLength = 0;

        for (uint256 i; i < buffer.length; ++i) {
            bytes1 char = bytes1(_unsafeReadBytesOffset(buffer, i));
            if (((SPECIAL_CHARS_LOOKUP & (1 << uint8(char))) != 0)) {
                output[outputLength++] = "\\";
                if (char == 0x08) output[outputLength++] = "b";
                else if (char == 0x09) output[outputLength++] = "t";
                else if (char == 0x0a) output[outputLength++] = "n";
                else if (char == 0x0c) output[outputLength++] = "f";
                else if (char == 0x0d) output[outputLength++] = "r";
                else if (char == 0x5c) output[outputLength++] = "\\";
                else if (char == 0x22) {
                    // solhint-disable-next-line quotes
                    output[outputLength++] = '"';
                }
            } else {
                output[outputLength++] = char;
            }
        }
        // write the actual length and deallocate unused memory
        assembly ("memory-safe") {
            mstore(output, outputLength)
            mstore(0x40, add(output, shl(5, shr(5, add(outputLength, 63)))))
        }

        return string(output);
    }

    /**
     * @dev Reads a bytes32 from a bytes array without bounds checking.
     *
     * NOTE: making this function internal would mean it could be used with memory unsafe offset, and marking the
     * assembly block as such would prevent some optimizations.
     */
    function _unsafeReadBytesOffset(bytes memory buffer, uint256 offset) private pure returns (bytes32 value) {
        // This is not memory safe in the general case, but all calls to this private function are within bounds.
        assembly ("memory-safe") {
            value := mload(add(add(buffer, 0x20), offset))
        }
    }
}

contracts/MembershipPaymentManager.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title MembershipPaymentManager
 * @notice Manages ERC20 token payments for role-based access control memberships
 * @dev Supports multiple payment tokens with configurable pricing and routing
 * 
 * Key Features:
 * - Multiple ERC20 payment methods
 * - Adjustable pricing per role/tier
 * - Payment routing to treasury/multiple recipients
 * - Admin functions for refunds and emergency recovery
 * - Events for complete audit trail
 */
contract MembershipPaymentManager is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // ========== Role Definitions ==========

    bytes32 public constant PAYMENT_ADMIN_ROLE = keccak256("PAYMENT_ADMIN_ROLE");
    bytes32 public constant PRICING_ADMIN_ROLE = keccak256("PRICING_ADMIN_ROLE");
    bytes32 public constant TREASURY_ADMIN_ROLE = keccak256("TREASURY_ADMIN_ROLE");

    // ========== Payment Configuration ==========

    struct PaymentToken {
        address tokenAddress;
        bool isActive;
        uint8 decimals;
        string symbol;
    }

    struct RolePricing {
        mapping(address => uint256) priceByToken; // token address => price
        bool isActive;
    }

    struct PaymentRouting {
        address recipient;
        uint256 basisPoints; // 10000 = 100%
    }

    // Payment tokens (token address => PaymentToken info)
    mapping(address => PaymentToken) public paymentTokens;
    address[] public paymentTokenList;

    // Role pricing (role hash => RolePricing)
    mapping(bytes32 => RolePricing) private rolePricing;

    // Payment routing (array of recipients and their share)
    PaymentRouting[] public paymentRouting;

    // Treasury address (receives payments if no routing configured)
    address public treasury;

    // ========== Payment Tracking ==========

    struct Payment {
        address buyer;
        bytes32 role;
        address paymentToken;
        uint256 amount;
        uint256 timestamp;
        uint8 tier; // 0 for non-tiered, 1-4 for tiers
        bool isRefunded; // explicit refund flag
    }

    // Payment tracking
    mapping(bytes32 => Payment) public payments; // paymentId => Payment
    mapping(address => bytes32[]) public userPayments; // user => paymentIds
    uint256 public totalPaymentsCount;

    // Revenue tracking by token
    mapping(address => uint256) public revenueByToken;

    // ========== Events ==========

    event PaymentTokenAdded(address indexed token, string symbol, uint8 decimals);
    event PaymentTokenUpdated(address indexed token, bool isActive);
    event PaymentTokenRemoved(address indexed token);
    event RolePriceUpdated(bytes32 indexed role, address indexed token, uint256 price);
    event PaymentProcessed(
        bytes32 indexed paymentId,
        address indexed buyer,
        bytes32 indexed role,
        address paymentToken,
        uint256 amount,
        uint8 tier
    );
    event PaymentRefunded(bytes32 indexed paymentId, address indexed buyer, uint256 amount);
    event PaymentRoutingUpdated(address indexed recipient, uint256 basisPoints);
    event PaymentRoutingCleared();
    event TreasuryUpdated(address indexed oldTreasury, address indexed newTreasury);
    event FundsWithdrawn(address indexed token, address indexed recipient, uint256 amount);
    event EmergencyWithdrawal(address indexed token, address indexed recipient, uint256 amount);

    // ========== Constructor ==========

    constructor(address _treasury) {
        require(_treasury != address(0), "Invalid treasury address");

        treasury = _treasury;

        // Grant deployer all admin roles
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAYMENT_ADMIN_ROLE, msg.sender);
        _grantRole(PRICING_ADMIN_ROLE, msg.sender);
        _grantRole(TREASURY_ADMIN_ROLE, msg.sender);

        // Set up role hierarchy
        _setRoleAdmin(PAYMENT_ADMIN_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(PRICING_ADMIN_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(TREASURY_ADMIN_ROLE, DEFAULT_ADMIN_ROLE);
    }

    // ========== Payment Token Management ==========

    /**
     * @notice Add a new payment token
     * @param token Address of the ERC20 token
     * @param symbol Token symbol for display
     * @param decimals Token decimals
     */
    function addPaymentToken(
        address token,
        string memory symbol,
        uint8 decimals
    ) external onlyRole(PAYMENT_ADMIN_ROLE) {
        require(token != address(0), "Invalid token address");
        require(bytes(symbol).length > 0, "Invalid token symbol");
        require(paymentTokens[token].tokenAddress == address(0), "Token already exists");

        paymentTokens[token] = PaymentToken({
            tokenAddress: token,
            isActive: true,
            decimals: decimals,
            symbol: symbol
        });

        paymentTokenList.push(token);

        emit PaymentTokenAdded(token, symbol, decimals);
    }

    /**
     * @notice Update payment token active status
     * @param token Address of the token
     * @param isActive New active status
     */
    function setPaymentTokenActive(
        address token,
        bool isActive
    ) external onlyRole(PAYMENT_ADMIN_ROLE) {
        require(paymentTokens[token].tokenAddress != address(0), "Token not found");

        paymentTokens[token].isActive = isActive;

        emit PaymentTokenUpdated(token, isActive);
    }

    /**
     * @notice Remove a payment token (soft delete - mark as inactive)
     * @param token Address of the token
     */
    function removePaymentToken(address token) external onlyRole(PAYMENT_ADMIN_ROLE) {
        require(paymentTokens[token].tokenAddress != address(0), "Token not found");

        paymentTokens[token].isActive = false;

        emit PaymentTokenRemoved(token);
    }

    // ========== Pricing Management ==========

    /**
     * @notice Set price for a role in a specific token
     * @param role Role identifier
     * @param token Payment token address
     * @param price Price in token's smallest unit
     */
    function setRolePrice(
        bytes32 role,
        address token,
        uint256 price
    ) external onlyRole(PRICING_ADMIN_ROLE) {
        require(paymentTokens[token].isActive, "Token not active");

        rolePricing[role].priceByToken[token] = price;
        rolePricing[role].isActive = true;

        emit RolePriceUpdated(role, token, price);
    }

    /**
     * @notice Set prices for a role in multiple tokens at once
     * @param role Role identifier
     * @param tokens Array of payment token addresses
     * @param prices Array of prices in each token's smallest unit
     */
    function setRolePrices(
        bytes32 role,
        address[] calldata tokens,
        uint256[] calldata prices
    ) external onlyRole(PRICING_ADMIN_ROLE) {
        require(tokens.length == prices.length, "Arrays length mismatch");

        // Validate all tokens before making any state changes
        for (uint256 i = 0; i < tokens.length; i++) {
            require(paymentTokens[tokens[i]].isActive, "Token not active");
        }

        // All validations passed, now set prices
        for (uint256 i = 0; i < tokens.length; i++) {
            rolePricing[role].priceByToken[tokens[i]] = prices[i];
            emit RolePriceUpdated(role, tokens[i], prices[i]);
        }

        rolePricing[role].isActive = true;
    }

    /**
     * @notice Get price for a role in a specific token
     * @param role Role identifier
     * @param token Payment token address
     * @return price Price in token's smallest unit
     */
    function getRolePrice(bytes32 role, address token) external view returns (uint256) {
        return rolePricing[role].priceByToken[token];
    }

    // ========== Payment Routing Management ==========

    /**
     * @notice Set payment routing configuration
     * @param recipients Array of recipient addresses
     * @param basisPoints Array of basis points (10000 = 100%)
     */
    function setPaymentRouting(
        address[] calldata recipients,
        uint256[] calldata basisPoints
    ) external onlyRole(TREASURY_ADMIN_ROLE) {
        require(recipients.length == basisPoints.length, "Arrays length mismatch");

        // Clear existing routing
        delete paymentRouting;

        uint256 totalBasisPoints = 0;
        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "Invalid recipient");

            paymentRouting.push(PaymentRouting({
                recipient: recipients[i],
                basisPoints: basisPoints[i]
            }));

            totalBasisPoints += basisPoints[i];

            emit PaymentRoutingUpdated(recipients[i], basisPoints[i]);
        }

        require(totalBasisPoints == 10000, "Basis points must sum to 10000");
    }

    /**
     * @notice Clear payment routing (send all to treasury)
     */
    function clearPaymentRouting() external onlyRole(TREASURY_ADMIN_ROLE) {
        delete paymentRouting;
        emit PaymentRoutingCleared();
    }

    /**
     * @notice Update treasury address
     * @param newTreasury New treasury address
     */
    function setTreasury(address newTreasury) external onlyRole(TREASURY_ADMIN_ROLE) {
        require(newTreasury != address(0), "Invalid treasury address");
        address oldTreasury = treasury;
        treasury = newTreasury;
        emit TreasuryUpdated(oldTreasury, newTreasury);
    }

    // ========== Payment Processing ==========

    /**
     * @notice Process payment for a role purchase
     * @param payer Address that will transfer the tokens (usually msg.sender or contract holding tokens)
     * @param buyer Address of the actual buyer (for tracking purposes)
     * @param role Role identifier
     * @param paymentToken Token used for payment
     * @param amount Amount to pay
     * @param tier Tier level (0 for non-tiered)
     * @return paymentId Unique payment identifier
     */
    function processPayment(
        address payer,
        address buyer,
        bytes32 role,
        address paymentToken,
        uint256 amount,
        uint8 tier
    ) external nonReentrant whenNotPaused returns (bytes32) {
        require(payer != address(0), "Invalid payer");
        require(buyer != address(0), "Invalid buyer");
        require(amount > 0, "Amount must be greater than 0");
        require(paymentTokens[paymentToken].isActive, "Payment token not active");
        require(rolePricing[role].isActive, "Role pricing not configured");

        uint256 requiredAmount = rolePricing[role].priceByToken[paymentToken];
        require(requiredAmount > 0, "Price not set for this token");
        require(amount >= requiredAmount, "Insufficient payment amount");

        // Transfer tokens from payer to this contract
        IERC20(paymentToken).safeTransferFrom(payer, address(this), amount);

        // Generate payment ID
        bytes32 paymentId = keccak256(
            abi.encodePacked(buyer, role, paymentToken, amount, block.timestamp, totalPaymentsCount)
        );

        // Record payment
        payments[paymentId] = Payment({
            buyer: buyer,
            role: role,
            paymentToken: paymentToken,
            amount: amount,
            timestamp: block.timestamp,
            tier: tier,
            isRefunded: false
        });

        userPayments[buyer].push(paymentId);
        totalPaymentsCount++;

        // Route payment
        _routePayment(paymentToken, amount);

        // Track revenue
        revenueByToken[paymentToken] += amount;

        emit PaymentProcessed(paymentId, buyer, role, paymentToken, amount, tier);

        return paymentId;
    }

    /**
     * @notice Route payment to recipients or treasury
     * @param token Payment token address
     * @param amount Total amount to route
     */
    function _routePayment(address token, uint256 amount) internal {
        if (paymentRouting.length == 0) {
            // No routing configured, send to treasury
            IERC20(token).safeTransfer(treasury, amount);
        } else {
            // Route according to configuration
            uint256 totalDistributed = 0;
            for (uint256 i = 0; i < paymentRouting.length; i++) {
                uint256 share = (amount * paymentRouting[i].basisPoints) / 10000;
                if (share > 0) {
                    IERC20(token).safeTransfer(paymentRouting[i].recipient, share);
                    totalDistributed += share;
                }
            }

            // Send any remainder due to rounding to the last recipient
            uint256 remainder = amount - totalDistributed;
            if (remainder > 0 && paymentRouting.length > 0) {
                address lastRecipient = paymentRouting[paymentRouting.length - 1].recipient;
                IERC20(token).safeTransfer(lastRecipient, remainder);
            }
        }
    }

    // ========== Refund Management ==========

    /**
     * @notice Refund a payment (admin function for dispute resolution)
     * @param paymentId Payment to refund
     */
    function refundPayment(bytes32 paymentId) external onlyRole(PAYMENT_ADMIN_ROLE) nonReentrant {
        Payment storage payment = payments[paymentId];
        require(payment.buyer != address(0), "Payment not found");
        require(!payment.isRefunded, "Payment already refunded");

        address buyer = payment.buyer;
        address token = payment.paymentToken;
        uint256 amount = payment.amount;

        // Mark as refunded
        payment.isRefunded = true;

        // Reduce revenue tracking - this must never underflow
        require(revenueByToken[token] >= amount, "Revenue underflow on refund");
        revenueByToken[token] -= amount;

        // Transfer funds back to buyer
        IERC20(token).safeTransfer(buyer, amount);

        emit PaymentRefunded(paymentId, buyer, amount);
    }

    // ========== Treasury Management ==========

    /**
     * @notice Withdraw accumulated funds to treasury
     * @param token Token to withdraw
     */
    function withdrawToTreasury(address token) external onlyRole(TREASURY_ADMIN_ROLE) nonReentrant {
        require(paymentTokens[token].tokenAddress != address(0), "Token not configured");

        uint256 balance = IERC20(token).balanceOf(address(this));
        require(balance > 0, "No balance to withdraw");

        IERC20(token).safeTransfer(treasury, balance);

        emit FundsWithdrawn(token, treasury, balance);
    }

    /**
     * @notice Emergency withdrawal function (only DEFAULT_ADMIN)
     * @param token Token to withdraw
     * @param recipient Recipient address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(
        address token,
        address recipient,
        uint256 amount
    ) external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant {
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be > 0");

        IERC20(token).safeTransfer(recipient, amount);

        emit EmergencyWithdrawal(token, recipient, amount);
    }

    // ========== View Functions ==========

    /**
     * @notice Get list of all payment tokens
     * @return tokens Array of token addresses
     */
    function getPaymentTokens() external view returns (address[] memory) {
        return paymentTokenList;
    }

    /**
     * @notice Get user's payment history
     * @param user User address
     * @return paymentIds Array of payment IDs
     */
    function getUserPayments(address user) external view returns (bytes32[] memory) {
        return userPayments[user];
    }

    /**
     * @notice Get payment routing configuration
     * @return recipients Array of recipient addresses
     * @return basisPoints Array of basis points
     */
    function getPaymentRouting() external view returns (
        address[] memory recipients,
        uint256[] memory basisPoints
    ) {
        uint256 length = paymentRouting.length;
        recipients = new address[](length);
        basisPoints = new uint256[](length);

        for (uint256 i = 0; i < length; i++) {
            recipients[i] = paymentRouting[i].recipient;
            basisPoints[i] = paymentRouting[i].basisPoints;
        }
    }

    /**
     * @notice Get contract balance for a token
     * @param token Token address
     * @return balance Token balance
     */
    function getBalance(address token) external view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    // ========== Emergency Functions ==========

    /**
     * @notice Pause contract
     */
    function pause() external onlyRole(PAYMENT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @notice Unpause contract
     */
    function unpause() external onlyRole(PAYMENT_ADMIN_ROLE) {
        _unpause();
    }
}

contracts/RoleManager.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./MembershipPaymentManager.sol";
import "./ZKKeyManager.sol";

/**
 * @title RoleManager
 * @notice Comprehensive role-based access control system with hierarchy, timelocks, and multisig support
 * @dev Implements enterprise-grade RBAC following principle of least privilege
 * 
 * Role Hierarchy (highest to lowest):
 * 1. DEFAULT_ADMIN_ROLE - Contract owner, manages all roles
 * 2. CORE_SYSTEM_ADMIN_ROLE - Critical upgrades, high-threshold multisig
 * 3. OPERATIONS_ADMIN_ROLE - Day-to-day operations, medium-threshold multisig
 * 4. EMERGENCY_GUARDIAN_ROLE - Emergency pause/cancel, low-threshold multisig
 * 5. Function-specific roles (MARKET_MAKER_ROLE, CLEARPATH_USER_ROLE, TOKENMINT_ROLE)
 * 6. OVERSIGHT_COMMITTEE_ROLE - Independent verification body
 */
contract RoleManager is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // Safe Singleton Factory address for deterministic deployments
    address internal constant SAFE_SINGLETON_FACTORY = 0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7;

    // Tracks whether role metadata has been initialized (for lazy init pattern)
    bool internal _roleMetadataInitialized;

    // ========== Role Definitions ==========

    // Administrative Roles (Hierarchical)
    bytes32 public constant CORE_SYSTEM_ADMIN_ROLE = keccak256("CORE_SYSTEM_ADMIN_ROLE");
    bytes32 public constant OPERATIONS_ADMIN_ROLE = keccak256("OPERATIONS_ADMIN_ROLE");
    bytes32 public constant EMERGENCY_GUARDIAN_ROLE = keccak256("EMERGENCY_GUARDIAN_ROLE");

    // Function-Specific Roles (Granular Permissions)
    bytes32 public constant MARKET_MAKER_ROLE = keccak256("MARKET_MAKER_ROLE");
    bytes32 public constant CLEARPATH_USER_ROLE = keccak256("CLEARPATH_USER_ROLE");
    bytes32 public constant TOKENMINT_ROLE = keccak256("TOKENMINT_ROLE");
    bytes32 public constant FRIEND_MARKET_ROLE = keccak256("FRIEND_MARKET_ROLE");

    // Oversight & Verification
    bytes32 public constant OVERSIGHT_COMMITTEE_ROLE = keccak256("OVERSIGHT_COMMITTEE_ROLE");

    // ========== Payment Integration ==========

    MembershipPaymentManager public paymentManager;

    // ========== ZK Key Management ==========

    ZKKeyManager public zkKeyManager;

    // ========== Role Metadata ==========

    struct RoleMetadata {
        string name;
        string description;
        uint256 minApprovals; // Minimum approvals required (multisig threshold)
        uint256 timelockDelay; // Minimum delay before action execution (in seconds)
        bool isPremium; // Whether this role requires payment
        uint256 price; // Price in wei (if premium)
        bool isActive; // Whether role assignments are currently active
        uint256 maxMembers; // Maximum number of users with this role (0 = unlimited)
        uint256 currentMembers; // Current number of users with this role
    }

    mapping(bytes32 => RoleMetadata) public roleMetadata;

    // ========== Timelock Management ==========

    struct PendingAction {
        bytes32 actionId;
        bytes32 role;
        address target;
        bool isGrant; // true for grant, false for revoke
        uint256 executeAfter;
        uint256 approvalCount;
        mapping(address => bool) approvals;
        bool executed;
        bool cancelled;
    }

    mapping(bytes32 => PendingAction) public pendingActions;
    bytes32[] public pendingActionIds;

    // ========== Role Purchase Management ==========

    struct RolePurchase {
        address buyer;
        bytes32 role;
        uint256 timestamp;
        uint256 price;
        string zkPublicKey; // Optional ZK key for ClearPath users
    }

    mapping(address => mapping(bytes32 => RolePurchase)) public purchases;
    mapping(address => bytes32[]) public userPurchasedRoles;

    // ========== Events ==========

    event RoleMetadataUpdated(bytes32 indexed role, string name, uint256 minApprovals, uint256 timelockDelay);
    event RolePurchased(address indexed buyer, bytes32 indexed role, uint256 price, uint256 timestamp);
    event RolePurchasedWithToken(address indexed buyer, bytes32 indexed role, address indexed paymentToken, uint256 price, uint256 timestamp);
    event ZKKeyRegistered(address indexed user, bytes32 indexed role, string zkPublicKey);
    event ActionProposed(bytes32 indexed actionId, bytes32 indexed role, address indexed target, bool isGrant);
    event ActionApproved(bytes32 indexed actionId, address indexed approver);
    event ActionExecuted(bytes32 indexed actionId, bytes32 indexed role, address indexed target, bool isGrant);
    event ActionCancelled(bytes32 indexed actionId, address indexed canceller);
    event EmergencyPaused(address indexed guardian);
    event EmergencyUnpaused(address indexed admin);
    event PaymentManagerUpdated(address indexed oldManager, address indexed newManager);
    event ZKKeyManagerUpdated(address indexed oldManager, address indexed newManager);
    event ZKKeyRotated(address indexed user, string newZKPublicKey);

    // ========== Constructor ==========

    constructor() {
        // Grant deployer the default admin role
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

        // Set up role hierarchy
        _setRoleAdmin(CORE_SYSTEM_ADMIN_ROLE, DEFAULT_ADMIN_ROLE);
        _setRoleAdmin(OPERATIONS_ADMIN_ROLE, CORE_SYSTEM_ADMIN_ROLE);
        _setRoleAdmin(EMERGENCY_GUARDIAN_ROLE, OPERATIONS_ADMIN_ROLE);
        _setRoleAdmin(MARKET_MAKER_ROLE, OPERATIONS_ADMIN_ROLE);
        _setRoleAdmin(CLEARPATH_USER_ROLE, OPERATIONS_ADMIN_ROLE);
        _setRoleAdmin(TOKENMINT_ROLE, OPERATIONS_ADMIN_ROLE);
        _setRoleAdmin(FRIEND_MARKET_ROLE, OPERATIONS_ADMIN_ROLE);
        _setRoleAdmin(OVERSIGHT_COMMITTEE_ROLE, DEFAULT_ADMIN_ROLE);

        // NOTE: Role metadata initialization removed from constructor to reduce
        // deployment gas for deterministic (CREATE2) deployments on low-gas-limit chains.
        // Call initializeRoleMetadata() after deployment.
    }

    /**
     * @notice Initialize role metadata (lazy init for gas-constrained deployments)
     * @dev Can only be called once by an admin. Should be called after deployment.
     */
    function initializeRoleMetadata() external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(!_roleMetadataInitialized, "Role metadata already initialized");
        _roleMetadataInitialized = true;
        _initializeRoleMetadata();
    }

    // ========== Role Metadata Initialization ==========

    function _initializeRoleMetadata() internal {
        // Core System Admin: High security, long timelock
        roleMetadata[CORE_SYSTEM_ADMIN_ROLE] = RoleMetadata({
            name: "Core System Admin",
            description: "Critical upgrades and system changes",
            minApprovals: 3, // High threshold
            timelockDelay: 7 days, // Long delay for critical actions
            isPremium: false,
            price: 0,
            isActive: true,
            maxMembers: 5, // Limited to small group
            currentMembers: 0
        });

        // Operations Admin: Medium security, medium timelock
        roleMetadata[OPERATIONS_ADMIN_ROLE] = RoleMetadata({
            name: "Operations Admin",
            description: "Day-to-day operations and configurations",
            minApprovals: 2, // Medium threshold
            timelockDelay: 2 days,
            isPremium: false,
            price: 0,
            isActive: true,
            maxMembers: 10,
            currentMembers: 0
        });

        // Emergency Guardian: Low threshold, short timelock (rapid response)
        roleMetadata[EMERGENCY_GUARDIAN_ROLE] = RoleMetadata({
            name: "Emergency Guardian",
            description: "Emergency pause and cancel capabilities",
            minApprovals: 1, // Low threshold for rapid response
            timelockDelay: 1 hours, // Minimal delay
            isPremium: false,
            price: 0,
            isActive: true,
            maxMembers: 7,
            currentMembers: 0
        });

        // Market Maker: Premium, function-specific
        roleMetadata[MARKET_MAKER_ROLE] = RoleMetadata({
            name: "Market Maker",
            description: "Create and manage prediction markets",
            minApprovals: 1,
            timelockDelay: 0, // No timelock for functional roles
            isPremium: true,
            price: 100 ether, // 100 tokens (adjust as needed)
            isActive: true,
            maxMembers: 0, // Unlimited
            currentMembers: 0
        });

        // ClearPath User: Premium, function-specific
        roleMetadata[CLEARPATH_USER_ROLE] = RoleMetadata({
            name: "ClearPath User",
            description: "Access to DAO governance platform",
            minApprovals: 1,
            timelockDelay: 0,
            isPremium: true,
            price: 250 ether, // 250 tokens
            isActive: true,
            maxMembers: 0, // Unlimited
            currentMembers: 0
        });

        // Token Mint: Premium, function-specific
        roleMetadata[TOKENMINT_ROLE] = RoleMetadata({
            name: "Token Mint",
            description: "Mint and manage NFTs and ERC20 tokens",
            minApprovals: 1,
            timelockDelay: 0,
            isPremium: true,
            price: 150 ether, // 150 tokens
            isActive: true,
            maxMembers: 0, // Unlimited
            currentMembers: 0
        });

        // Friend Market: Premium, function-specific
        roleMetadata[FRIEND_MARKET_ROLE] = RoleMetadata({
            name: "Friend Market Creator",
            description: "Create small-scale friend group prediction markets",
            minApprovals: 1,
            timelockDelay: 0,
            isPremium: true,
            price: 50 ether, // 50 tokens (base price, tiers managed by TieredRoleManager)
            isActive: true,
            maxMembers: 0, // Unlimited
            currentMembers: 0
        });

        // Oversight Committee: Independent verification
        roleMetadata[OVERSIGHT_COMMITTEE_ROLE] = RoleMetadata({
            name: "Oversight Committee",
            description: "Independent verification and approval",
            minApprovals: 2,
            timelockDelay: 1 days,
            isPremium: false,
            price: 0,
            isActive: true,
            maxMembers: 7,
            currentMembers: 0
        });
    }

    // ========== Role Purchase Functions ==========

    /**
     * @notice Purchase a premium role with ETH (legacy method)
     * @param role The role to purchase
     */
    function purchaseRole(bytes32 role) external payable nonReentrant whenNotPaused {
        RoleMetadata storage metadata = roleMetadata[role];

        require(metadata.isActive, "Role is not active");
        require(metadata.isPremium, "Role is not purchasable");
        require(msg.value >= metadata.price, "Insufficient payment");
        require(!hasRole(role, msg.sender), "Already has role");
        require(metadata.maxMembers == 0 || metadata.currentMembers < metadata.maxMembers, "Role at max capacity");

        // Record purchase
        purchases[msg.sender][role] = RolePurchase({
            buyer: msg.sender,
            role: role,
            timestamp: block.timestamp,
            price: msg.value,
            zkPublicKey: "" // Can be set later via registerZKKey
        });

        userPurchasedRoles[msg.sender].push(role);

        // Grant role immediately (no timelock for purchases)
        _grantRole(role, msg.sender);
        metadata.currentMembers++;

        emit RolePurchased(msg.sender, role, msg.value, block.timestamp);

        // Refund excess payment
        if (msg.value > metadata.price) {
            payable(msg.sender).transfer(msg.value - metadata.price);
        }
    }

    /**
     * @notice Purchase a premium role with ERC20 token
     * @param role The role to purchase
     * @param paymentToken The ERC20 token to use for payment
     * @param amount The amount of tokens to pay
     */
    function purchaseRoleWithToken(
        bytes32 role,
        address paymentToken,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        require(address(paymentManager) != address(0), "Payment manager not set");

        RoleMetadata storage metadata = roleMetadata[role];

        require(metadata.isActive, "Role is not active");
        require(metadata.isPremium, "Role is not purchasable");
        require(!hasRole(role, msg.sender), "Already has role");
        require(metadata.maxMembers == 0 || metadata.currentMembers < metadata.maxMembers, "Role at max capacity");

        // Transfer tokens from buyer to this contract
        IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), amount);

        // Approve payment manager to transfer tokens from this contract
        IERC20(paymentToken).safeIncreaseAllowance(address(paymentManager), amount);

        // Process payment through payment manager (payment manager will transfer from this contract)
        bytes32 paymentId = paymentManager.processPayment(
            address(this), // payer is this contract (we already have the tokens)
            msg.sender,    // buyer is the actual user
            role,
            paymentToken,
            amount,
            0 // tier 0 for non-tiered purchases
        );

        // Record purchase
        purchases[msg.sender][role] = RolePurchase({
            buyer: msg.sender,
            role: role,
            timestamp: block.timestamp,
            price: amount,
            zkPublicKey: "" // Can be set later via registerZKKey
        });

        userPurchasedRoles[msg.sender].push(role);

        // Grant role immediately (no timelock for purchases)
        _grantRole(role, msg.sender);
        metadata.currentMembers++;

        emit RolePurchasedWithToken(msg.sender, role, paymentToken, amount, block.timestamp);
    }

    /**
     * @notice Register ZK public key for ClearPath users
     * @param zkPublicKey The zero-knowledge public key
     */
    function registerZKKey(string memory zkPublicKey) external whenNotPaused {
        require(hasRole(CLEARPATH_USER_ROLE, msg.sender), "Must have ClearPath role");
        require(bytes(zkPublicKey).length > 0, "Invalid ZK key");

        // If ZKKeyManager is set, use production key management
        if (address(zkKeyManager) != address(0)) {
            // Register key with ZKKeyManager for production verification
            zkKeyManager.registerKeyFor(msg.sender, zkPublicKey);
        }

        // Store in local purchases mapping for backward compatibility
        purchases[msg.sender][CLEARPATH_USER_ROLE].zkPublicKey = zkPublicKey;

        emit ZKKeyRegistered(msg.sender, CLEARPATH_USER_ROLE, zkPublicKey);
    }

    /**
     * @notice Rotate ZK public key to a new key
     * @param newZKPublicKey The new zero-knowledge public key
     */
    function rotateZKKey(string memory newZKPublicKey) external whenNotPaused {
        require(hasRole(CLEARPATH_USER_ROLE, msg.sender), "Must have ClearPath role");
        require(bytes(newZKPublicKey).length > 0, "Invalid ZK key");
        require(address(zkKeyManager) != address(0), "ZK key manager not set");

        // Rotate key using ZKKeyManager
        zkKeyManager.rotateKeyFor(msg.sender, newZKPublicKey);

        // Update local purchases mapping
        purchases[msg.sender][CLEARPATH_USER_ROLE].zkPublicKey = newZKPublicKey;

        emit ZKKeyRotated(msg.sender, newZKPublicKey);
    }

    /**
     * @notice Revoke ZK public key
     */
    function revokeZKKey() external whenNotPaused {
        require(hasRole(CLEARPATH_USER_ROLE, msg.sender), "Must have ClearPath role");
        require(address(zkKeyManager) != address(0), "ZK key manager not set");

        // Revoke key using ZKKeyManager - pass msg.sender as the user
        // Note: This works because ZKKeyManager allows key owner to revoke their own key
        zkKeyManager.revokeKey(msg.sender);

        // Clear local purchases mapping
        purchases[msg.sender][CLEARPATH_USER_ROLE].zkPublicKey = "";
    }

    // ========== Timelock & Multisig Functions ==========

    /**
     * @notice Propose a role grant/revoke action (subject to timelock and multisig)
     * @param role The role to grant/revoke
     * @param target The address to grant/revoke the role to/from
     * @param isGrant True for grant, false for revoke
     */
    function proposeRoleAction(
        bytes32 role,
        address target,
        bool isGrant
    ) external onlyRole(getRoleAdmin(role)) whenNotPaused returns (bytes32) {
        require(target != address(0), "Invalid target address");

        RoleMetadata storage metadata = roleMetadata[role];
        require(metadata.isActive, "Role is not active");

        // Skip timelock for non-premium roles with no timelock delay
        if (!metadata.isPremium && metadata.timelockDelay == 0) {
            if (isGrant) {
                _grantRole(role, target);
                metadata.currentMembers++;
            } else {
                _revokeRole(role, target);
                if (metadata.currentMembers > 0) metadata.currentMembers--;
            }
            return bytes32(0);
        }

        // Generate unique action ID
        bytes32 actionId = keccak256(abi.encodePacked(role, target, isGrant, block.timestamp));

        PendingAction storage action = pendingActions[actionId];
        action.actionId = actionId;
        action.role = role;
        action.target = target;
        action.isGrant = isGrant;
        action.executeAfter = block.timestamp + metadata.timelockDelay;
        action.approvalCount = 1; // Proposer's approval
        action.approvals[msg.sender] = true;

        pendingActionIds.push(actionId);

        emit ActionProposed(actionId, role, target, isGrant);
        emit ActionApproved(actionId, msg.sender);

        return actionId;
    }

    /**
     * @notice Approve a pending role action
     * @param actionId The ID of the action to approve
     */
    function approveRoleAction(bytes32 actionId) external whenNotPaused {
        PendingAction storage action = pendingActions[actionId];

        require(action.actionId != bytes32(0), "Action does not exist");
        require(!action.executed, "Action already executed");
        require(!action.cancelled, "Action cancelled");
        require(!action.approvals[msg.sender], "Already approved");
        require(hasRole(getRoleAdmin(action.role), msg.sender), "Not authorized to approve");

        action.approvals[msg.sender] = true;
        action.approvalCount++;

        emit ActionApproved(actionId, msg.sender);
    }

    /**
     * @notice Execute a pending role action after timelock
     * @param actionId The ID of the action to execute
     */
    function executeRoleAction(bytes32 actionId) external nonReentrant whenNotPaused {
        PendingAction storage action = pendingActions[actionId];

        require(action.actionId != bytes32(0), "Action does not exist");
        require(!action.executed, "Action already executed");
        require(!action.cancelled, "Action cancelled");
        require(block.timestamp >= action.executeAfter, "Timelock not expired");

        RoleMetadata storage metadata = roleMetadata[action.role];
        require(action.approvalCount >= metadata.minApprovals, "Insufficient approvals");

        action.executed = true;

        if (action.isGrant) {
            _grantRole(action.role, action.target);
            metadata.currentMembers++;
        } else {
            _revokeRole(action.role, action.target);
            if (metadata.currentMembers > 0) metadata.currentMembers--;
        }

        emit ActionExecuted(actionId, action.role, action.target, action.isGrant);
    }

    /**
     * @notice Cancel a pending action (Emergency Guardian only)
     * @param actionId The ID of the action to cancel
     */
    function cancelRoleAction(bytes32 actionId) external onlyRole(EMERGENCY_GUARDIAN_ROLE) {
        PendingAction storage action = pendingActions[actionId];

        require(action.actionId != bytes32(0), "Action does not exist");
        require(!action.executed, "Action already executed");
        require(!action.cancelled, "Action already cancelled");

        action.cancelled = true;

        emit ActionCancelled(actionId, msg.sender);
    }

    // ========== Emergency Functions ==========

    /**
     * @notice Emergency pause (Guardian only)
     */
    function emergencyPause() external onlyRole(EMERGENCY_GUARDIAN_ROLE) {
        _pause();
        emit EmergencyPaused(msg.sender);
    }

    /**
     * @notice Unpause contract (Admin only)
     */
    function unpause() external onlyRole(OPERATIONS_ADMIN_ROLE) {
        _unpause();
        emit EmergencyUnpaused(msg.sender);
    }

    // ========== Admin Functions ==========

    /**
     * @notice Set the payment manager contract
     * @param _paymentManager Address of MembershipPaymentManager contract
     */
    function setPaymentManager(address _paymentManager) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_paymentManager != address(0), "Invalid payment manager address");
        address oldManager = address(paymentManager);
        paymentManager = MembershipPaymentManager(_paymentManager);
        emit PaymentManagerUpdated(oldManager, _paymentManager);
    }

    /**
     * @notice Set the ZK key manager contract
     * @param _zkKeyManager Address of ZKKeyManager contract
     */
    function setZKKeyManager(address _zkKeyManager) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_zkKeyManager != address(0), "Invalid ZK key manager address");
        address oldManager = address(zkKeyManager);
        zkKeyManager = ZKKeyManager(_zkKeyManager);
        emit ZKKeyManagerUpdated(oldManager, _zkKeyManager);
    }

    /**
     * @notice Update role metadata (Core System Admin only)
     */
    function updateRoleMetadata(
        bytes32 role,
        string memory name,
        string memory description,
        uint256 minApprovals,
        uint256 timelockDelay,
        uint256 maxMembers
    ) external onlyRole(CORE_SYSTEM_ADMIN_ROLE) {
        RoleMetadata storage metadata = roleMetadata[role];

        metadata.name = name;
        metadata.description = description;
        metadata.minApprovals = minApprovals;
        metadata.timelockDelay = timelockDelay;
        metadata.maxMembers = maxMembers;

        emit RoleMetadataUpdated(role, name, minApprovals, timelockDelay);
    }

    /**
     * @notice Set role price (Operations Admin only)
     */
    function setRolePrice(bytes32 role, uint256 price) external onlyRole(OPERATIONS_ADMIN_ROLE) {
        require(roleMetadata[role].isPremium, "Role is not premium");
        roleMetadata[role].price = price;
    }

    /**
     * @notice Toggle role active status (Operations Admin only)
     */
    function setRoleActive(bytes32 role, bool isActive) external onlyRole(OPERATIONS_ADMIN_ROLE) {
        roleMetadata[role].isActive = isActive;
    }

    /**
     * @notice Withdraw contract balance (Operations Admin only)
     */
    function withdraw() external onlyRole(OPERATIONS_ADMIN_ROLE) nonReentrant {
        uint256 balance = address(this).balance;
        require(balance > 0, "No balance to withdraw");
        payable(msg.sender).transfer(balance);
    }

    // ========== Override AccessControl Functions to Enforce Governance ==========

    /**
     * @notice Override grantRole to enforce timelock/multisig governance
     * @dev Only allows direct grants for premium roles via purchaseRole or internal grants via executeRoleAction
     */
    function grantRole(bytes32 role, address account) public virtual override {
        // Only allow direct grants in specific scenarios:
        // 1. During contract initialization (for setting up initial admin hierarchy)
        // 2. For premium roles purchased via purchaseRole (checked by msg.sender == this)
        // 3. For roles with no timelock delay (executed immediately in proposeRoleAction)
        // 4. Role admin can directly grant roles for initial setup (to simplify testing and initial deployment)

        RoleMetadata storage metadata = roleMetadata[role];

        // Allow if called internally (from purchaseRole or executeRoleAction)
        if (msg.sender == address(this)) {
            super.grantRole(role, account);
            return;
        }

        // Allow role admin to grant for initial setup and testing
        if (hasRole(getRoleAdmin(role), msg.sender)) {
            super.grantRole(role, account);
            return;
        }

        // For premium roles, users must use purchaseRole
        if (metadata.isPremium) {
            revert("Premium roles must be purchased via purchaseRole");
        }

        // Otherwise reject
        revert("Must have role admin permission or use governance flow");
    }

    /**
     * @notice Override revokeRole to maintain consistency with grantRole
     * @dev Allows direct revocations by role admin or internal calls
     */
    function revokeRole(bytes32 role, address account) public virtual override {
        // Allow if called internally (from executeRoleAction)
        if (msg.sender == address(this)) {
            super.revokeRole(role, account);
            return;
        }

        // Allow role admin to revoke
        if (hasRole(getRoleAdmin(role), msg.sender)) {
            super.revokeRole(role, account);
            return;
        }

        // Otherwise reject
        revert("Must have role admin permission");
    }

    /**
     * @notice Internal function to grant role (bypasses checks)
     * @dev Used by executeRoleAction and purchaseRole
     */
    function _internalGrantRole(bytes32 role, address account) internal {
        super.grantRole(role, account);
    }

    /**
     * @notice Internal function to revoke role (bypasses checks)
     * @dev Used by executeRoleAction
     */
    function _internalRevokeRole(bytes32 role, address account) internal {
        super.revokeRole(role, account);
    }

    // ========== View Functions ==========

    /**
     * @notice Get role metadata
     */
    function getRoleMetadata(bytes32 role) external view returns (RoleMetadata memory) {
        return roleMetadata[role];
    }

    /**
     * @notice Get user's purchased roles
     */
    function getUserPurchasedRoles(address user) external view returns (bytes32[] memory) {
        return userPurchasedRoles[user];
    }

    /**
     * @notice Get ZK public key for user
     */
    function getZKPublicKey(address user) external view returns (string memory) {
        // If ZKKeyManager is set, get key from there
        if (address(zkKeyManager) != address(0)) {
            return zkKeyManager.getPublicKey(user);
        }
        // Otherwise fall back to local storage
        return purchases[user][CLEARPATH_USER_ROLE].zkPublicKey;
    }

    /**
     * @notice Check if user has a valid ZK key
     */
    function hasValidZKKey(address user) external view returns (bool) {
        if (address(zkKeyManager) != address(0)) {
            return zkKeyManager.hasValidKey(user);
        }
        // Fall back to checking local storage
        return bytes(purchases[user][CLEARPATH_USER_ROLE].zkPublicKey).length > 0;
    }

    /**
     * @notice Get pending action count
     */
    function getPendingActionCount() external view returns (uint256) {
        return pendingActionIds.length;
    }

    /**
     * @notice Check if action is approved by address
     */
    function isActionApprovedBy(bytes32 actionId, address approver) external view returns (bool) {
        return pendingActions[actionId].approvals[approver];
    }
}

contracts/TieredRoleManager.sol.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./RoleManager.sol";

/**
 * @title TieredRoleManager
 * @notice Extends RoleManager with tiered membership levels (Bronze, Silver, Gold, Platinum)
 * @dev Each product role can have multiple tiers with different permissions and limits
 */
contract TieredRoleManager is RoleManager {
    using SafeERC20 for IERC20;

    // NOTE: SAFE_SINGLETON_FACTORY is inherited from RoleManager

    bool private _initialized;

    bool private _marketMakerTiersInitialized;
    bool private _clearPathTiersInitialized;
    bool private _tokenMintTiersInitialized;
    bool private _friendMarketTiersInitialized;

    // ========== Tier Definitions ==========

    enum MembershipTier {
        NONE,       // 0 - No membership
        BRONZE,     // 1 - Basic tier
        SILVER,     // 2 - Intermediate tier
        GOLD,       // 3 - Advanced tier
        PLATINUM    // 4 - Premium tier
    }

    // ========== Tier Metadata ==========

    struct TierLimits {
        uint256 dailyBetLimit;           // Max bets per day
        uint256 weeklyBetLimit;          // Max bets per week
        uint256 monthlyMarketCreation;   // Max markets created per month
        uint256 maxPositionSize;         // Max position size in wei
        uint256 maxConcurrentMarkets;    // Max active markets at once
        uint256 withdrawalLimit;         // Daily withdrawal limit
        bool canCreatePrivateMarkets;    // Can create private markets
        bool canUseAdvancedFeatures;     // Access to advanced features
        uint256 feeDiscount;             // Fee discount in basis points (100 = 1%)
    }

    struct TierMetadata {
        string name;
        string description;
        uint256 price;                   // Upgrade price from previous tier
        TierLimits limits;
        bool isActive;
    }

    // role => tier => TierMetadata
    mapping(bytes32 => mapping(MembershipTier => TierMetadata)) public tierMetadata;

    // user => role => current tier
    mapping(address => mapping(bytes32 => MembershipTier)) public userTiers;

    // user => role => tier => purchase timestamp
    mapping(address => mapping(bytes32 => mapping(MembershipTier => uint256))) public tierPurchases;

    // ========== Membership Duration Tracking ==========

    enum MembershipDuration {
        ONE_MONTH,      // 30 days
        THREE_MONTHS,   // 90 days
        SIX_MONTHS,     // 180 days
        TWELVE_MONTHS,  // 365 days
        ENTERPRISE      // Custom/unlimited duration
    }

    // user => role => membership expiration timestamp
    mapping(address => mapping(bytes32 => uint256)) public membershipExpiration;

    // user => role => membership duration type
    mapping(address => mapping(bytes32 => MembershipDuration)) public membershipDurationType;

    // ========== Usage Tracking ==========

    struct UsageStats {
        uint256 dailyBetsCount;
        uint256 weeklyBetsCount;
        uint256 monthlyMarketsCreated;
        uint256 dailyWithdrawals;
        uint256 activeMarketsCount;
        uint256 lastDailyReset;
        uint256 lastWeeklyReset;
        uint256 lastMonthlyReset;
    }

    // user => role => usage stats
    mapping(address => mapping(bytes32 => UsageStats)) public usageStats;

    // ========== Events ==========

    event TierPurchased(address indexed user, bytes32 indexed role, MembershipTier tier, uint256 price);
    event TierUpgraded(address indexed user, bytes32 indexed role, MembershipTier fromTier, MembershipTier toTier);
    event UsageLimitExceeded(address indexed user, bytes32 indexed role, string limitType);
    event UsageRecorded(address indexed user, bytes32 indexed role, string actionType);
    event MembershipExtended(address indexed user, bytes32 indexed role, uint256 newExpiration, MembershipDuration duration);
    event MembershipExpired(address indexed user, bytes32 indexed role);
    event TierPriceUpdated(bytes32 indexed role, MembershipTier tier, uint256 newPrice);
    event TierLimitsUpdated(bytes32 indexed role, MembershipTier tier);
    event TierMetadataUpdated(bytes32 indexed role, MembershipTier tier, string name, string description);
    event TierActiveStatusChanged(bytes32 indexed role, MembershipTier tier, bool active);

    // ========== Constructor ==========

    constructor() RoleManager() {
        // For direct deployments, prevent initialize() from being called.
        // For Safe Singleton Factory (CREATE2) deployments, allow a one-time initialize()
        // so DEFAULT_ADMIN_ROLE isn't stuck on the factory.
        _initialized = msg.sender != SAFE_SINGLETON_FACTORY;
    }

    /**
     * @notice Initialize admin after deterministic deployment (CREATE2)
     * @dev Only callable once, intended for Safe Singleton Factory deployments.
     */
    function initialize(address admin) external {
        require(!_initialized, "Already initialized");
        require(admin != address(0), "Invalid admin");

        _initialized = true;

        // RoleManager constructor granted DEFAULT_ADMIN_ROLE to the deployer (the factory).
        // Hand it over to the intended admin and revoke the factory.
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _revokeRole(DEFAULT_ADMIN_ROLE, SAFE_SINGLETON_FACTORY);
    }

    /**
     * @notice Initialize tier metadata in smaller chunks.
     * @dev Tier metadata initialization writes a lot of storage and can exceed low block gas limits
     *      when done inside a constructor. These functions allow initializing the tiers post-deploy.
     */

    function initializeMarketMakerTiers() external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(!_marketMakerTiersInitialized, "Market maker tiers already initialized");
        _marketMakerTiersInitialized = true;
        _initializeMarketMakerTiers();
    }

    function initializeClearPathTiers() external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(!_clearPathTiersInitialized, "ClearPath tiers already initialized");
        _clearPathTiersInitialized = true;
        _initializeClearPathTiers();
    }

    function initializeTokenMintTiers() external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(!_tokenMintTiersInitialized, "Token mint tiers already initialized");
        _tokenMintTiersInitialized = true;
        _initializeTokenMintTiers();
    }

    function initializeFriendMarketTiers() external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(!_friendMarketTiersInitialized, "Friend market tiers already initialized");
        _friendMarketTiersInitialized = true;
        _initializeFriendMarketTiers();
    }

    // ========== Tier Metadata Initialization ==========

    function _initializeTierMetadata() internal {
        // MARKET_MAKER Role Tiers
        _initializeMarketMakerTiers();

        // CLEARPATH_USER Role Tiers
        _initializeClearPathTiers();

        // TOKENMINT Role Tiers
        _initializeTokenMintTiers();

        // FRIEND_MARKET Role Tiers
        _initializeFriendMarketTiers();
    }

    function _initializeMarketMakerTiers() internal {
        bytes32 role = MARKET_MAKER_ROLE;

        // Bronze Tier - Basic market maker
        tierMetadata[role][MembershipTier.BRONZE] = TierMetadata({
            name: "Market Maker Bronze",
            description: "Basic market creation capabilities",
            price: 100 ether,
            limits: TierLimits({
                dailyBetLimit: 10,
                weeklyBetLimit: 50,
                monthlyMarketCreation: 5,
                maxPositionSize: 10 ether,
                maxConcurrentMarkets: 3,
                withdrawalLimit: 50 ether,
                canCreatePrivateMarkets: false,
                canUseAdvancedFeatures: false,
                feeDiscount: 0 // No discount
            }),
            isActive: true
        });

        // Silver Tier - Intermediate market maker
        tierMetadata[role][MembershipTier.SILVER] = TierMetadata({
            name: "Market Maker Silver",
            description: "Enhanced market creation with more limits",
            price: 150 ether, // Upgrade cost from Bronze
            limits: TierLimits({
                dailyBetLimit: 25,
                weeklyBetLimit: 150,
                monthlyMarketCreation: 15,
                maxPositionSize: 50 ether,
                maxConcurrentMarkets: 10,
                withdrawalLimit: 200 ether,
                canCreatePrivateMarkets: false,
                canUseAdvancedFeatures: true,
                feeDiscount: 500 // 5% discount
            }),
